import React, { Component } from 'react';

class Input extends Component {
    constructor(props) {
        super(props)

        this.state = {
            task: ''
        }
    }

    handleInputChange = (event) => {
        this.setState({
            task: event.target.value
        })
        console.log(this.state.task) 

    }

    render() {
        return (
            <div>
                <h1>User Input Space</h1>
                <form>
                    <input type="text" value={this.state.task} onChange={this.handleInputChange} placeholder="Enter Task" />
                    <button>Add</button>
                </form>

            </div>
        );
    }
}
export default Input;
